import { Component, Inject } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { EmployeePosition } from 'src/app/enums/employeePosition ';
import { Employee } from 'src/app/models/employee';
import { EmployeeService } from 'src/app/services/employee.service';

@Component({
  selector: 'app-employee-update',
  templateUrl: './employee-update.component.html',
  styleUrls: ['./employee-update.component.scss']
})
export class EmployeeUpdateComponent {

  employeeForm: FormGroup;

  title = "Update Employee"

  employeePosition = [
    { number: EmployeePosition.Menager, name: "Menager" },
    { number: EmployeePosition.Seller, name: "Seller " },
    { number: EmployeePosition.Technician, name: "Techniciane" },
    { number: EmployeePosition.OfficeWorker, name: "Office worker" },
    { number: EmployeePosition.Driver, name: "Driver" }
  ]

  constructor(private formBuilder: FormBuilder,
    private employeeService: EmployeeService,
    private dialogRef: MatDialogRef<EmployeeUpdateComponent>,
    @Inject(MAT_DIALOG_DATA) public data: Employee) {

    this.employeeForm = this.formBuilder.group({
      firstName: [data.firstName, Validators.required],
      lastName: [data.lastName, Validators.required],
      salary: [data.salary, Validators.required],
      email: [data.email, [Validators.required, Validators.email]],
      employeePosition: [data.employeePosition, Validators.required],
    });
  }

  updateEmployee(employee: Employee) {
    this.employeeService.updateEmployeeService(this.data.shopId, this.data.employeeId, employee).subscribe(
      () => {
        this.dialogRef.close();
      },
      error => {
        console.error('Error:', error);
      }
    );
  }
}
<div class="d-flex justify-content-between p-2">
  <h2>{{ title }}</h2>
  <i class="bi bi-x-circle" style="font-size: 1.5rem" mat-dialog-close></i>
</div>
<form
  [formGroup]="employeeForm"
  (ngSubmit)="updateEmployee(employeeForm.value)"
>
  <div class="p-3 m-0 border-0 m-0 border-0">
    <div class="mb-3">
      <mat-form-field class="example-full-width">
        <mat-label>First Name:</mat-label>
        <input matInput formControlName="firstName" required />
      </mat-form-field>
    </div>

    <div class="mb-3">
      <mat-form-field class="example-full-width">
        <mat-label>Last Name:</mat-label>
        <input matInput formControlName="lastName" required />
      </mat-form-field>
    </div>

    <div class="mb-3">
      <mat-form-field class="example-full-width">
        <mat-label>Salary:</mat-label>
        <input matInput formControlName="salary" type="number" required />
      </mat-form-field>
    </div>

    <div class="mb-3">
      <mat-form-field class="example-full-width">
        <mat-label>Email:</mat-label>
        <input matInput formControlName="email" required />
      </mat-form-field>
    </div>

    <div class="mb-3">
      <mat-form-field class="example-full-width">
        <mat-label>Employee Position</mat-label>
        <mat-select formControlName="employeePosition" required>
          <mat-option
            *ngFor="let position of employeePosition"
            [value]="position.number"
          >
            {{ position.name }}
          </mat-option>
        </mat-select>
      </mat-form-field>
    </div>
  </div>
  <div class="d-flex justify-content-end p-2">
    <button style="margin-right: 8px" mat-raised-button mat-dialog-close>
      Cancel
    </button>
    <button
      mat-raised-button
      color="primary"
      type="submit"
      [disabled]="employeeForm.invalid"
    >
      Submit
    </button>
  </div>
</form>
