import { Component, Inject } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { AddressCreate } from 'src/app/models/address-create';
import { ProductFormComponent } from 'src/app/products/product-form/product-form.component';
import { AddressService } from 'src/app/services/address.service';

@Component({
  selector: 'app-adress-form',
  templateUrl: './adress-form.component.html',
  styleUrls: ['./adress-form.component.scss']
})
export class AdressFormComponent {

  addressForm: FormGroup;
  title = "Add Address";

  constructor(private fb: FormBuilder, private addressService: AddressService,
    private dialogRef: MatDialogRef<AdressFormComponent>, @Inject(MAT_DIALOG_DATA) public shopId: number) {

    this.addressForm = this.fb.group({
      city: ['', Validators.required],
      street: ['', Validators.required],
      number: ['', Validators.required],
      zipCode: ['', Validators.required]
    });
  }

  submit(address: AddressCreate) {
    this.addressService.postAddressForShopService(this.shopId, address).subscribe(
      () => {
        this.dialogRef.close();
      },
      error => {
        console.error('Error:', error);
      }
    );
  }
}
<div class="d-flex justify-content-between p-2">
  <h2>{{ title }}</h2>
  <i class="bi bi-x-circle" style="font-size: 1.5rem" mat-dialog-close></i>
</div>

<form [formGroup]="addressForm" (ngSubmit)="submit(addressForm.value)">
  <div class="p-3 m-0 border-0 m-0 border-0">
    <div class="mb-3">
      <mat-form-field class="example-full-width">
        <mat-label>City:</mat-label>
        <input matInput formControlName="city" required />
      </mat-form-field>
    </div>

    <div class="mb-3">
      <mat-form-field class="example-full-width">
        <mat-label>Street:</mat-label>
        <input matInput formControlName="street" required />
      </mat-form-field>
    </div>

    <div class="mb-3">
      <mat-form-field class="example-full-width">
        <mat-label>Number:</mat-label>
        <input matInput formControlName="number" required />
      </mat-form-field>
    </div>

    <div class="mb-3">
      <mat-form-field class="example-full-width">
        <mat-label>Zip Code:</mat-label>
        <input matInput formControlName="zipCode" required />
      </mat-form-field>
    </div>
  </div>

  <div class="d-flex justify-content-end p-2">
    <button style="margin-right: 8px" mat-raised-button mat-dialog-close>
      Cancel
    </button>
    <button
      mat-raised-button
      color="primary"
      type="submit"
      [disabled]="addressForm.invalid"
    >
      Submit
    </button>
  </div>
</form>
