import { Component } from '@angular/core';
import { MatTableDataSource } from '@angular/material/table';
import { Shop } from 'src/app/models/shop';
import { ShopService } from 'src/app/services/shop.service';
import { ShopFormComponent } from '../shop-form/shop-form.component';
import { MatDialog } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Country } from 'src/app/enums/country';
import { ShopCreate } from 'src/app/models/shop-create';
import { ShopUpdateComponent } from '../shop-update/shop-update.component';
import { ProductService } from 'src/app/services/product.service';
import { ProductFormComponent } from 'src/app/products/product-form/product-form.component';
import { Router } from '@angular/router';
import { AdressFormComponent } from '../adress-form/adress-form.component';
import { EmployeeFormComponent } from 'src/app/employee/employee-form/employee-form.component';
import { ConfirmationModalComponent } from 'src/app/shared/confirmation-modal/confirmation-modal.component';
import { PageEvent } from '@angular/material/paginator';

@Component({
  selector: 'app-shop-table',
  templateUrl: './shop-table.component.html',
  styleUrls: ['./shop-table.component.scss'],
})
export class ShopTableComponent {
  dataSource: MatTableDataSource<Shop>;
  value: string = '';

  displayedColumns: string[] = [
    'shopId',
    'name',
    'startDate',
    'country',
    'actions',
  ];

  inputDisabled: boolean = false;

  countryValues = [
    { number: Country.Poland, name: 'Poland' },
    { number: Country.Germany, name: 'Germany' },
    { number: Country.France, name: 'France' },
  ];

  selectedValues: number[] = [];

  pageIndex: number = 1;
  pageSize: number = 10;
  length: number = 0;

  constructor(
    private shopService: ShopService,
    private dialog: MatDialog,
    private snackBar: MatSnackBar,
    private productService: ProductService,
    private router: Router
  ) {
    this.dataSource = new MatTableDataSource<Shop>([]);
  }

  ngOnInit() {
    this.getShops('');

    this.shopService.getShopForProduct().subscribe((shops: Shop[]) => {
      this.length = shops.length;
    });
  }

  onSelectedValuesChange() {
    this.inputDisabled = this.selectedValues.length > 0;
    this.getShops('', this.selectedValues);
  }

  getCountryName(countryValue: number): string {
    return Country[countryValue];
  }

  clearSearch() {
    this.value = '';
    this.getShops('');
  }

  getLengthShops() {
    this.shopService.getShopForProduct().subscribe((shops: Shop[]) => {
      this.length = shops.length;
    });
  }

  getShops(searchTerm?: string, selectedValues?: number[]) {
    this.shopService
      .getShopsService(
        this.pageIndex,
        this.pageSize,
        searchTerm,
        selectedValues
      )
      .subscribe(
        (result: Shop[]) => {
          this.dataSource = new MatTableDataSource(result);
        },
        (error) => {
          console.error('Błąd podczas pobierania produktów:', error);
        }
      );
  }

  createShop() {
    const dialogRef = this.dialog.open(ShopFormComponent, {
      width: '400px',
      height: '450px',
    });
    dialogRef.afterClosed().subscribe((result) => {
      this.getShops('');
      this.getLengthShops();
    });
  }

  updateShop(shop: Shop) {
    const dialogRef = this.dialog.open(ShopUpdateComponent, {
      width: '400px',
      height: '450px',
      data: shop,
    });
    dialogRef.afterClosed().subscribe((result) => {
      this.getShops('');
    });
  }

  deleteShop(shopId: number) {
    const dialogRef = this.dialog.open(ConfirmationModalComponent, {
      width: '400px',
      height: '200px',
      data: {
        titleText: 'Delete Shop',
        confirmationText: 'Do you really want delete Shop?',
      },
    });

    dialogRef.afterClosed().subscribe((res) => {
      if (res) {
        this.shopService.deleteShopService(shopId).subscribe(
          (result) => {
            this.getShops('');
            this.snackBar.open('Shop deleted successfully', 'Close', {
              duration: 3000,
            });
          },
          (error) => {
            this.snackBar.open('Error deleting shop', 'Close', {
              duration: 3000,
            });
          }
        );
      }
    });
  }

  createProduct(shopId: number) {
    const dialogRef = this.dialog.open(ProductFormComponent, {
      width: '400px',
      height: '450px',
      data: shopId,
    });
    dialogRef.afterClosed().subscribe((result) => {
      this.getShops('');
    });
  }

  informationsAboutShop(shopId: number) {
    this.router.navigate(['/additional-informations', shopId]);
  }

  createAddress(shopId: number) {
    const dialogRef = this.dialog.open(AdressFormComponent, {
      width: '400px',
      height: '550px',
      data: shopId,
    });
  }

  createEmployee(shopId: number) {
    const dialogRef = this.dialog.open(EmployeeFormComponent, {
      width: '400px',
      height: '650px',
      data: shopId,
    });
  }

  onPageChange(event: PageEvent) {
    this.pageIndex = event.pageIndex + 1;
    this.pageSize = event.pageSize;

    this.getShops('', this.selectedValues);
  }
}
<div class="row mt-5">
  <div class="col-md-6">
    <mat-form-field class="example-form-field">
      <input
        matInput
        type="text"
        placeholder="Search"
        [(ngModel)]="value"
        [disabled]="inputDisabled"
      />
    </mat-form-field>

    <button
      style="margin-left: 10px"
      mat-raised-button
      (click)="getShops(value)"
    >
      Search
    </button>
    <button style="margin-left: 10px" mat-raised-button (click)="clearSearch()">
      Clear
    </button>
    <mat-button-toggle-group
      multiple
      style="margin-left: 10px"
      [(ngModel)]="selectedValues"
      (ngModelChange)="onSelectedValuesChange()"
    >
      <mat-button-toggle
        *ngFor="let item of countryValues"
        [value]="item.number"
        >{{ item.name }}</mat-button-toggle
      >
    </mat-button-toggle-group>
  </div>
  <div class="col-md-6 d-flex justify-content-end">
    <button mat-raised-button (click)="createShop()">Create shop</button>
  </div>
</div>
<table mat-table [dataSource]="dataSource" class="mat-elevation-z8">
  <!-- Define columns -->
  <ng-container matColumnDef="shopId">
    <th mat-header-cell *matHeaderCellDef>Id</th>
    <td mat-cell *matCellDef="let element">{{ element.shopId }}</td>
  </ng-container>

  <ng-container matColumnDef="name">
    <th mat-header-cell *matHeaderCellDef>Name</th>
    <td mat-cell *matCellDef="let element">{{ element.name }}</td>
  </ng-container>

  <ng-container matColumnDef="startDate">
    <th mat-header-cell *matHeaderCellDef>Date</th>
    <td mat-cell *matCellDef="let element">
      {{ element.startDate | date: "yyyy-MM-dd" }}
    </td>
  </ng-container>

  <ng-container matColumnDef="country">
    <th mat-header-cell *matHeaderCellDef>Country</th>
    <td mat-cell *matCellDef="let element">
      {{ getCountryName(element.country) }}
    </td>
  </ng-container>

  <ng-container matColumnDef="actions">
    <th mat-header-cell *matHeaderCellDef>Actions</th>
    <td mat-cell *matCellDef="let element">
      <button mat-icon-button (click)="informationsAboutShop(element.shopId)">
        <i class="bi bi-info-circle" style="font-size: 1.5rem"></i>
      </button>
      <button
        mat-icon-button
        (click)="deleteShop(element.shopId)"
        matTooltip="Delete Shop"
      >
        <i class="bi bi-trash" style="font-size: 1.5rem"></i>
      </button>
      <button
        mat-icon-button
        (click)="updateShop(element)"
        matTooltip="Update Shop"
      >
        <i class="bi bi-envelope" style="font-size: 1.5rem"></i>
      </button>
      <button
        mat-icon-button
        (click)="createProduct(element.shopId)"
        matTooltip="Create Product"
      >
        <i class="bi bi-cart2" style="font-size: 1.5rem"></i>
      </button>
      <button
        mat-icon-button
        (click)="createAddress(element.shopId)"
        matTooltip="Add Address"
      >
        <i class="bi bi-house" style="font-size: 1.5rem"></i>
      </button>
      <button
        mat-icon-button
        (click)="createEmployee(element.shopId)"
        matTooltip="Add Employee"
      >
        <i class="bi bi-person-fill-add" style="font-size: 1.5rem"></i>
      </button>
    </td>
  </ng-container>

  <!-- Header and Row definitions -->
  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns"></tr>
</table>
<mat-paginator
  [length]="length"
  [pageSize]="pageSize"
  [pageSizeOptions]="[5, 10, 15]"
  (page)="onPageChange($event)"
></mat-paginator>
